	-- Arquivo de apoio, caso você queira criar tabelas como as aqui criadas para a API funcionar.
	-- Você precisa executar os comandos no banco de dados para criar as tabelas,
	-- ter este arquivo aqui não significa que a tabela em seu BD estará como abaixo!

	/*
	comandos para mysql server
	*/

	CREATE DATABASE f1;
    
	USE f1;
    
	CREATE TABLE equipe (
		idEquipe INT PRIMARY KEY AUTO_INCREMENT,
		nome VARCHAR(50),
		logo VARCHAR(200)
	);

	CREATE TABLE piloto (
		idPiloto INT PRIMARY KEY AUTO_INCREMENT,
		NumPiloto INT,
		imgNumPiloto VARCHAR(200),
        imgPiloto VARCHAR(200),
		nome VARCHAR(50),
		dataNasc DATE, 
		titulos INT, 
		fkEquipe INT, 
		historia TEXT,
		FOREIGN KEY (fkEquipe) REFERENCES equipe(idEquipe)
);

	CREATE TABLE usuario (
		idUsuario INT PRIMARY KEY AUTO_INCREMENT,
		nome VARCHAR(50),
		email VARCHAR(50),
		senha VARCHAR(50),
		imgPerfil VARCHAR(100),
		fkEquipe INT,
		fkPiloto INT,
		FOREIGN KEY (fkEquipe) REFERENCES equipe(idEquipe),
		FOREIGN KEY (fkPiloto) REFERENCES piloto(idPiloto)
	);

	CREATE TABLE quiz (
	idQuiz int PRIMARY KEY AUTO_INCREMENT, 
	AnoQuiz int
	);

	CREATE TABLE tentativa(
	idQuiz int,
	idUsuario int, 
	acertos int,
	erros int,
	tempo float,
	FOREIGN KEY (idQuiz) REFERENCES quiz(idQuiz),
	FOREIGN KEY (idUsuario) REFERENCES usuario(idUsuario),
	CONSTRAINT usuarioQuiz PRIMARY KEY (idQuiz, idUsuario));

